INSERT INTO `expert_ratings` (`id`, `campaign_id`, `expert_id`, `rating`, `comment`, `created_at`) VALUES
(1, 17, 24, 3, 'Ini cocok untuk dijadikan sebagai rekomendasi penyaluran dana yang utama karena orang di palestina perlu daging untuk memenuhi kebutuhan gizi', '2025-05-30 02:41:00'),
(2, 17, 25, 5, 'Daging merupakan kebutuhan gizi yang mumpuni, palestina yang terdampak perang kini memerlukan daging untuk mereka bertahan hidup. Harus dijadikan fokus utama selama 1000 hari kedepan', '2025-05-30 02:53:46'),
(3, 18, 25, 2, 'Menurut pertimbangan, penyaluran daging untuk pelosok negeri sepertinya kurang efektif, akan lebih baik jika di salurkan pada jenis makanan lain seperti beras, tepung, ataupun bahan makanan yang mudah dan tahan lama.', '2025-05-30 05:58:03'),
(4, 18, 24, 5, 'tentu sangat masuk dalam kategori sosial kenapa demikian karena ini juga sangat membantu bagi kau dhuafa didalam memenuhi kebutuhan pokok hidunya, dan sedekah daging ini juga merupakan suatu perbuatan yang dapat menumbuhkembangkan rasa empati, peduli terhadap sesama selain juga menumbuhkan rasa solidaritas diantara sesama manusia', '2025-06-03 01:17:14'),
(5, 19, 24, 5, 'Karenaa gaza terlalu hancur sebagai dampak perang israel jadi gaza tanpa masjid termasuk kategori kemanusiaan sama dakwah', '2025-06-03 01:23:35');
